mapper.select:
SELECT COUNT(*) AS count
FROM tb_checkin
WHERE user_id = #{userId}

    SELECT
      CAST(date AS CHAR) AS date,
	IF(status=1,"正常"."迟到") AS status
    FROM tb_checkin
    WHERE user_id=#{userId}
      AND date BETWEEN #{startDate} AND #{endDate}

SELECT date
FROM tb_holidays
WHERE date BETWEEN #{startDate} AND #{endDate}
